import styled from "styled-components/native";
import { View } from "react-native";

const positionVariant = {
  top: "marginTop",
  left: "marginLeft",
  right: "marginRight",
  bottom: "marginBottom",
};

const getVariant = (position, size) => {
  const alignPosition = positionVariant[position];

  return `${alignPosition}:${size}`;
};

export const Spacer = styled(View)`
  ${({ position, size }) => ``}
`;

Spacer.defaultProps = {
  position: "right",
  size: "8px",
};
