import React from "react";
import { Text, View, Image} from "react-native";
import { Card } from "react-native-paper";
import styled from "styled-components/native";
import Start from "../../../../assets/start";
import Open from "../../../../assets/open";

const Title = styled(Text)`
  font-family: ${(props) => props.theme.fonts.heading};
  font-size: ${(props) => props.theme.fontSizes.body};
  color: ${(props) => props.theme.colors.ui.primary};
`;

const Restaurantcard = styled(Card)`
  background-color: ${(props) => props.theme.colors.ui.primary};
`;

const RestaurantCardcover = styled(Card.Cover)`
  padding: ${(props) => props.theme.space[3]};
  background-color: ${(props) => props.theme.colors.ui.primary};
`;

const Address = styled(Text)`
  font-family: ${(props) => props.theme.fonts.body};
  font-size: ${(props) => props.theme.fontSizes.caption};
`;

const Info = styled(View)`
  padding: ${(props) => props.theme.space[3]};
`;

const Rating = styled.View`
  flex-direction: row;
  padding-top: ${(props) => props.theme.space[2]};
  padding-bottom: ${(props) => props.theme.space[2]};
  justify-content: flex-start;
`;
const Section = styled.View`
  display: 1;
  flex-direction: row;
  align-items: center;
`;

const SectionEnd = styled.View`
  flex-direction: row;
  justify-content: flex-end;
`;

export const RestaurantInfoCard = ({ restaurant = {} }) => {
  const {
    name = "Restaurant Pepito",
    icon,
    photos = [
      "https://cdn.vox-cdn.com/thumbor/HmsSKbk9Khv2MoO6j4rcqnWZVag=/0x0:5996x4003/620x465/filters:focal(1003x1633:1961x2591):format(webp)/cdn.vox-cdn.com/uploads/chorus_image/image/59732085/2021_03_23_Merois_008.12.jpg",
    ],
    address = "Some ramfom address",
    isOpenNow = true,
    rating = 5,
    isClosedTemporarily = true,
  } = restaurant;

  const ratingArray = Array.from(new Array(Math.floor(rating)));

  return (
    <Card elevation={5}>
      <Card.Cover key={name} source={{ uri: photos[0] }} />
      <Info>
        <Title>{name}</Title>
        <Section>
          <Rating>
            {ratingArray.map((start, startIndex) => (
              <Start width={20} heading={20} key={startIndex} />
            ))}
          </Rating>
          <SectionEnd>
            {isClosedTemporarily && (
              <Text variant="label" style={{ color: "red" }}>
                CLOSED TEMPORARILY
              </Text>
            )}
            {isOpenNow && <Open width={20} heading={20} />}
            <Image style={{width: 1}}/>
          </SectionEnd>
        </Section>
        <Address>{address}</Address>
      </Info>
    </Card>
  );
};
